import React, {useState} from 'react'
import Users from "../../Login/users.js";
import {useAppState} from "../../../app-context";
import { useForm } from 'react-hook-form';

function Form() {

    const {handleSubmit} = useForm();
    const appState = useAppState();

    const [id, setId] = useState(appState.user.id);
    const [login, setLogin] = useState(appState.user.login);
    const [password, setPassword] = useState(appState.user.password);
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [city, setCity] = useState("");

    const onSubmit = (data) => {
        const { email: login, password, id, name, surname, city } = data;
        appState.user.push({id: appState.user.id, login: appState.user.login, password: appState.user.password, });
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="form">
            <div>
                <label>ID</label>
                <input name="id" onChange={(e) => {setId(e.target.value);}} />
            </div>
            <div>
                <label>Login</label>
                <input name="login" onChange={(e) => {setLogin(e.target.value);}} />
            </div>
            <div>
                <label>Imię</label>
                <input name="name" onChange={(e) => {setName(e.target.value);}} />
            </div>
            <div>
                <label>Nazwisko</label>
                <input name="surname" onChange={(e) => {setId(e.target.value);}} />
            </div>
            <div>
                <label>Miasto</label>
                <input />
            </div>
            <div>
                <label>Hasło</label>
                <input />
            </div>
            <input type="submit" value="Zapisz"/>
        </form>
    )
}

export default Form
